    // });

    render() {
        const { products } = this.state;
        return (
            <div>
                <h1 id="title">Atualiza pre√ßo de Produto</h1>
                <table id="products">
                    {products.map((product, index) => (
                        <tr key={index}>
                            <td>{product.id}</td>
                            <td>{product.name}</td>
                            <td>{product.costPrice}</td>
                            <td>{product.sellPrice}</td>
                        </tr>
                    ))}
                </table>

            </div>
        );
    }
}
